import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ReplyForm from './ReplyForm';

function LetterGenerator({ userInfo }) {
  const [letter, setLetter] = useState('');
  const [reply, setReply] = useState('');
  const [aiResponse, setAiResponse] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    generateLetter(userInfo);
  }, [userInfo]);

  const generateLetter = async (info) => {
    try {
      const currentYear = new Date().getFullYear();
      const age = currentYear - new Date(info.birthday).getFullYear();

      const response = await axios.post('https://open.bigmodel.cn/api/paas/v4/chat/completions', {
        model: "glm-4",
        messages: [
          {
            role: "system",
            content: `你是一个AI助手，现在要扮演用户${info.year}年时的自己，给现在${age}岁的自己写一封信。请根据提供的信息，模仿当时的性格和说话方式。`
          },
          {
            role: "user",
            content: `我的生日是${info.birthday}，性别是${info.gender}。现在是${info.year}年的我，${info.situation}。
            请以${info.year}年的我的身份，写一封信给现在${age}岁的我。信中应该：
            1. 表达对现在这个年龄的我的看法和期望，比如你认为${age}岁的我应该是什么样子。
            2. 讲讲你对未来的期望和想象。
            3. 表达一些鼓励的话。
            4. 在信的结尾，鼓励现在的我回信讲讲现在的生活。
            请确保信的语气和表达方式符合${info.year}年时我的性格和说话方式。`
          }
        ],
        temperature: 0.7,
        top_p: 0.7,
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.REACT_APP_GLM_API_KEY}`
        }
      });

      setLetter(response.data.choices[0].message.content);
    } catch (err) {
      setError('生成信件时出错，请稍后再试。');
      console.error('Error generating letter:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleReply = async (replyText) => {
    setReply(replyText);
    setLoading(true);
    try {
      const currentYear = new Date().getFullYear();
      const age = currentYear - new Date(userInfo.birthday).getFullYear();

      const response = await axios.post('https://open.bigmodel.cn/api/paas/v4/chat/completions', {
        model: "glm-4",
        messages: [
          {
            role: "system",
            content: `你是一个AI助手，正在扮演用户${userInfo.year}年的自己。你刚刚给现在${age}岁的自己写了一封信。现在，你需要回应现在的自己的回信。请记住，你是过去的自己，不要混淆身份。保持${userInfo.year}年时的视角、知识水平和说话方式。`
          },
          {
            role: "assistant",
            content: `作为${userInfo.year}年的你，我刚刚写了这封信：\n\n${letter}`
          },
          {
            role: "user",
            content: `现在${age}岁的我回信说：${replyText}\n\n请以${userInfo.year}年的我的身份回应这封信。在回应时：
            1. 表达你对收到回信的感受。
            2. 对比你之前的期望和现实，表达你的想法。
            3. 如果现实与你的期望有差距，给予鼓励；如果超出期望，表达欣慰。
            4. 可以问一些你好奇的问题。
            5. 给现在的自己一些建议或祝福。
            请确保回信的语气和表达方式与你之前的信件一致，符合${userInfo.year}年时的你的性格和说话方式。记住，你还不知道现在发生的事情，只能基于你当时的期望和想象来回应。`
          }
        ],
        temperature: 0.6,
        top_p: 0.7,
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.REACT_APP_GLM_API_KEY}`
        }
      });

      setAiResponse(response.data.choices[0].message.content);
    } catch (err) {
      setError('生成回复时出错，请稍后再试。');
      console.error('Error generating reply:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p>正在生成信件，请稍候...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="letter-container">
      <div className="letter">
        <h2>来自过去的信</h2>
        <pre>{letter}</pre>
      </div>
      {!reply && <ReplyForm onSubmit={handleReply} />}
      {reply && (
        <div className="letter">
          <h2>你的回信</h2>
          <pre>{reply}</pre>
        </div>
      )}
      {aiResponse && (
        <div className="letter">
          <h2>过去的自己的回应</h2>
          <pre>{aiResponse}</pre>
        </div>
      )}
    </div>
  );
}

export default LetterGenerator;
