import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ReplyForm from './ReplyForm';
import WritingAnimation from './WritingAnimation';

function LetterGenerator({ userInfo }) {
  const [letter, setLetter] = useState('');
  const [reply, setReply] = useState('');
  const [aiResponse, setAiResponse] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [showPreviousLetters, setShowPreviousLetters] = useState(true);

  useEffect(() => {
    generateLetter(userInfo);
  }, [userInfo]);

  const generateLetter = async (info) => {
    try {
      const currentYear = new Date().getFullYear();
      const currentAge = currentYear - info.birthYear;
      const pastAge = info.pastYear - info.birthYear;

      const response = await axios.post('https://open.bigmodel.cn/api/paas/v4/chat/completions', {
        model: "glm-4",
        messages: [
          {
            role: "system",
            content: `你是一个AI助手，现在要扮演用户${info.pastYear}年时${pastAge}岁的自己，给现在${currentAge}岁的自己写一封信。请根据提供的信息，模仿当时的性格和说话方式。`
          },
          {
            role: "user",
            content: `我的昵称是${info.nickname}，出生于${info.birthYear}年，性别是${info.gender}。现在是${info.pastYear}年的我，${info.situation}。
            请以${info.pastYear}年${pastAge}岁的我的身份，写一封信给现在${currentAge}岁的我。信中应该：
            1. 表达对现在这个年龄的我的看法和期望，比如你认为${currentAge}岁的我应该是什么样子。
            2. 讲讲你对未来的期望和想象。
            3. 表达一些鼓励的话。
            4. 在信的结尾，鼓励现在的我回信讲讲现在的生活。
            请确保信的语气和表达方式符合${info.pastYear}年时我的性格和说话方式。使用"${info.nickname}"来称呼我。`
          }
        ],
        temperature: 0.7,
        top_p: 0.7,
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.REACT_APP_GLM_API_KEY}`
        }
      });

      setLetter(response.data.choices[0].message.content);
    } catch (err) {
      setError('生成信件时出错，请稍后再试。');
      console.error('Error generating letter:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleReply = async (replyText) => {
    setReply(replyText);
    setLoading(true);
    setShowPreviousLetters(false);
    try {
      const currentYear = new Date().getFullYear();
      const currentAge = currentYear - userInfo.birthYear;
      const pastAge = userInfo.pastYear - userInfo.birthYear;

      const response = await axios.post('https://open.bigmodel.cn/api/paas/v4/chat/completions', {
        model: "glm-4",
        messages: [
          {
            role: "system",
            content: `你是用户${userInfo.pastYear}年${pastAge}岁的自己。你刚刚给现在${currentAge}岁的自己写了一封信。现在，你需要回应现在的自己的回信。保持${userInfo.pastYear}年时的视角、知识水平，说话方式仿照用户提供的信息中行文方式，推理过去的他的说话方式。`
          },
          {
            role: "assistant",
            content: `作为${userInfo.pastYear}年${pastAge}岁的你，我刚刚写了这封信：\n\n${letter}`
          },
          {
            role: "user",
            content: `现在${currentAge}岁的我回信说：${replyText}\n\n请以${userInfo.pastYear}年${pastAge}岁的我的身份回应这封信。在回应时：
            最重要的是真实感，你要成为过去的那个自己，真心地关心现在的自己的处境。
            请确保回信的语气和表达方式与你之前的信件一致，符合${userInfo.pastYear}年时的你的性格和说话方式。记住，你还不知道现在发生的事情，只能基于你当时的期望和想象来回应。使用"${userInfo.nickname}"来称呼我。`
          }
        ],
        temperature: 0.6,
        top_p: 0.7,
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.REACT_APP_GLM_API_KEY}`
        }
      });

      setAiResponse(response.data.choices[0].message.content);
    } catch (err) {
      setError('生成回复时出错，请稍后再试。');
      console.error('Error generating reply:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <WritingAnimation year={userInfo.pastYear} />;
  if (error) return <p>{error}</p>;

  return (
    <div className="letter-container">
      {showPreviousLetters && (
        <>
          <div className="letter">
            <h2>来自过去的信</h2>
            <pre>{letter}</pre>
          </div>
          {reply && (
            <div className="letter">
              <h2>你的回信</h2>
              <pre>{reply}</pre>
            </div>
          )}
        </>
      )}
      {aiResponse && (
        <div className="letter">
          <h2>过去的自己的回应</h2>
          <pre>{aiResponse}</pre>
        </div>
      )}
      {!reply && <ReplyForm onSubmit={handleReply} />}
      {showPreviousLetters && aiResponse && (
        <button onClick={() => setShowPreviousLetters(!showPreviousLetters)}>
          {showPreviousLetters ? '折叠前面的信件' : '展开前面的信件'}
        </button>
      )}
    </div>
  );
}

export default LetterGenerator;
