import React, { useState, useEffect } from 'react';
import axios from 'axios';

function LetterGenerator({ userInfo }) {
  const [letter, setLetter] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const generateLetter = async () => {
      try {
        const response = await axios.post('https://open.bigmodel.cn/api/paas/v4/chat/completions', {
          model: "glm-4",
          messages: [
            {
              role: "system",
              content: "你是一个AI助手，现在要扮演用户过去的自己，根据用户提供的信息写一封信给现在的自己，行文要符合过去的自己的性格。讲讲你对现在的自己这个年龄的看法和期望，信的结尾鼓励现在的自己给过去的自己回信讲一下现在的生活。"
            },
            {
              role: "user",
              content: `我的生日是${userInfo.birthday}，性别是${userInfo.gender}。现在是${userInfo.year}年的我，${userInfo.situation}。请以过去的我的身份，写一封信给现在的我，表达对未来的期望和鼓励。`
            }
          ],
          temperature: 0.7,
          top_p: 0.7,
        }, {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${process.env.REACT_APP_GLM_API_KEY}`
          }
        });

        setLetter(response.data.choices[0].message.content);
      } catch (err) {
        setError('生成信件时出错，请稍后再试。');
        console.error('Error generating letter:', err);
      } finally {
        setLoading(false);
      }
    };

    generateLetter();
  }, [userInfo]);

  if (loading) return <p>正在生成信件，请稍候...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="letter">
      <h2>来自过去的信</h2>
      <pre>{letter}</pre>
    </div>
  );
}

export default LetterGenerator;
