import React from 'react';

function WritingAnimation({ age, nickname }) {
  return (
    <div className="writing-animation">
      <div className="pencil"></div>
      <p>{age}岁的{nickname}正在写信...</p>
    </div>
  );
}

export default WritingAnimation;
