import React, { useState, useEffect } from 'react';

const quotes = [
  { author: "爱因斯坦", quote: "想象力比知识更重要。" },
  { author: "孔子", quote: "学而不思则罔，思而不学则殆。" },
  { author: "莎士比亚", quote: "生活中最美好的事物是免费的。" },
  { author: "马克·吐温", quote: "二十年后，你会为没做的事情后悔，而不是为做了的事情后悔。" },
  { author: "甘地", quote: "成为你想看到的改变。" },
  // 可以继续添加更多名言...
];

function WritingAnimation() {
  const [quote, setQuote] = useState({ author: '', quote: '' });

  useEffect(() => {
    const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
    setQuote(randomQuote);
  }, []);

  return (
    <div className="writing-animation">
      <div className="typewriter">
        <div className="typewriter-text">正在穿越时空...</div>
      </div>
      <div className="quote">
        <p>"{quote.quote}"</p>
        <p className="author">—— {quote.author}</p>
      </div>
    </div>
  );
}

export default WritingAnimation;
